  position: relative;
}

.InfiniteCarouselFrame {
  width: 100%;
  overflow: hidden;
}

.InfiniteCarouselScrollTrack {
  overflow-x: scroll;
  overflow-y: hidden;
  white-space: nowrap;
  -webkit-overflow-scrolling: touch;
  overflow: -moz-scrollbars-none;
  -webkit-box-sizing: border-box;
}

.InfiniteCarouselScrollTrack::-webkit-scrollbar {
  display: none;
}

.InfiniteCarouselSlide img {
  width: 100%;
}

.InfiniteCarouselPaging {
  position: absolute;
  left: 50%;
  bottom: -60px;
  padding: 0;
  transform: translateX(-20%);
  font-family: Arial;
  letter-spacing: 2px;
  color: #888;
}

.InfiniteCarouselDots {
  position: absolute;
  left: 50%;
  bottom: 0;
  padding: 0;
  transform: translateX(-50%);
}

.InfiniteCarouselDot {
  display: inline-block;
  list-style: none;
  margin: 0 5px;
  border: 0;
  background: none;
  cursor: pointer;
}

.InfiniteCarouselDotIcon {
  display: block;
  background-color: #e5e5e5;
  width: 10px;
  height: 10px;
  border-radius: 50%;
}

.InfiniteCarouselDotActiveIcon {
  background-color: #48799a;
}

.InfiniteCarouselArrow {
  display: block;
  background: none;
  border: none;
  position: absolute;
  top: 50%;
  z-index: 2;
  outline: none;
  transform: translateY(-50%);
  cursor: pointer;
}

.InfiniteCarouselArrowPrev {
  left: 15px;
  right: auto;
}

.InfiniteCarouselArrowNext {
  left: auto;
  right: 15px;
}

.InfiniteCarouselArrowIcon {
  display: inline-block;
  padding: 10px;
  border: solid #e5e5e5;
  border-width: 0 5px 5px 0;
}

.InfiniteCarouselArrowNextIcon {
  transform: rotate(-45deg);
}

.InfiniteCarouselArrowPrevIcon {
  transform: rotate(135deg);
}
