import React, { Component } from 'react';
import '../Styles/ContactForm.css';
import bun from '../Assets/Bunny_hop.gif';

import Fade from 'react-reveal/Fade';

class ContactForm extends Component {
  //Need to figure out the triple input 
    state = {  } 

    render() { 
      window.addEventListener("load", function() {
        const form = document.getElementById('formInput');
        form.addEventListener("submit", function(e) {

          e.preventDefault();
          const data = new FormData(form);
          const action = e.target.action;
          fetch(action, {
            method: 'post',
            body: data,
          })
          .then(() => {
            var message = "Thank you! We will be in contact soon! (✿◠‿◠)";
            alert(message);
          })
        });
      });

        return (
           <div id = "contactForm">
           <Fade top><div><img className = "bunnyHop" src = {bun}></img></div></Fade>
                <div id = "contactFormInfo">


                        <div className = "getInTouch col-lg-7">
                            <p id = "title">Get In Touch</p>
                            <p id = "paragraph"> Thank you for viewing my resume! I would love to get in touch to work with you. Please fill out the form and we will be in contact soon!</p>

                        </div>

                        <div className = "formContainer col-lg-5">
                            <form id= "formInputs"
                            action = "https://script.google.com/macros/s/AKfycbwXvPXV2gI2hIc9EGqmgsXBv6xbvoHPLFlF5FufatTMnwxc0Yk0YY5tp7YmEXIDC5VDoQ/exec"
                            method = "post"
                            >
                            <label id = "formLabel" for="fname" required>Name:</label>
                            <input type="text" id="blank" name="name"></input>

                            <label id = "formLabel" for="fname" required>Email:</label>
                            <input type="text" id="blank" name="email"></input>

                            <label id = "formLabel" for="fname" required>Message:</label>
                            <textarea id = "blank" rows = "6" name = "message"></textarea>

                            <input id = "buttonSubmit" className = "submitButton" type="submit" value="Submit"></input>

                            </form>
                        </div>

                </div>



            </div>

        );
    }

}


export default ContactForm;
